mapper.insert:
INSERT INTO users (
username, password, email, nick_name, avatar_url,
total_runs, total_distance, total_duration, created_at, updated_at
) VALUES (
#{username}, #{password}, #{email}, #{nickName}, #{avatarUrl},
#{totalRuns}, #{totalDistance}, #{totalDuration}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM users
WHERE total_distance >= #{minDistance}
ORDER BY total_distance DESC

SELECT * FROM users
WHERE total_runs >= #{minRuns}
ORDER BY total_runs DESC
